package com.xr.coor

import adapter.FeedAdapter
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import model.Feed

class MainActivity : AppCompatActivity() {
    lateinit var list:ArrayList<Feed>
    lateinit var adapter:FeedAdapter
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        initView()
    }

    private fun initView() {
        loadList()
        val rvFeed = findViewById<RecyclerView>(R.id.rv_feed)
        rvFeed.layoutManager = LinearLayoutManager(this,LinearLayoutManager.VERTICAL,false)
        adapter = adapter.FeedAdapter(list)
    }

    private fun loadList() {
        list= ArrayList()
        list.add(
            Feed("data:image/jpeg;base64,","Mustafa","title","11:00","Hello","https://frankfurt.apollo.olxcdn.com/v1/files/zztiw2319i5j-UZ/image;s=1500x1000",400,156,false,true)
        )
    }
}